// frontend/src/components/UserManagement.js
import React, { useEffect, useState } from 'react';
import axios from 'axios';
import './Panel.css';

const UserManagement = () => {
  const [users, setUsers] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const token = localStorage.getItem('token');

  useEffect(() => {
    const fetchUsers = async () => {
      try {
        const response = await axios.get('http://localhost:5000/api/users', {
          headers: { Authorization: `Bearer ${token}` },
        });
        setUsers(response.data);
      } catch (err) {
        console.error('Error fetching users:', err);
        setError('Failed to load users.');
      } finally {
        setLoading(false);
      }
    };
    fetchUsers();
  }, [token]);

  const handleUnblock = async (id) => {
    try {
      await axios.patch(
        `http://localhost:5000/api/users/${id}/unblock`,
        {},
        { headers: { Authorization: `Bearer ${token}` } }
      );
      setUsers(users.map(user => (user.id === id ? { ...user, blocked: false } : user)));
      alert('User unblocked successfully.');
    } catch (err) {
      console.error('Error unblocking user:', err);
      setError('Failed to unblock user.');
    }
  };

  if (!token) return <div>You must log in first.</div>;
  if (loading) return <div>Loading...</div>;
  if (error) return <div>{error}</div>;

  return (
    <div className="panel">
      <h1>User Management</h1>
      <ul>
        {users.map((user) => (
          <li key={user.id} className="user-card">
            <h2>{user.name}</h2>
            <p>Email: {user.email}</p>
            <p>Role: {user.role}</p>
            <p>Status: {user.blocked ? 'Blocked' : 'Active'}</p>
            <button onClick={() => handleUnblock(user.id)} className={user.blocked ? 'unblock' : 'block'}>
              {user.blocked ? 'Unblock' : 'Block'}
            </button>
          </li>
        ))}
      </ul>
    </div>
  );
};

export default UserManagement;
