// frontend/src/components/UserPreferences.js
import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './UserPreferences.css';

const UserPreferences = () => {
  // Default preferences
  const defaultPreferences = {
    theme: 'light',
    notifications: true,
    language: 'en',
    timezone: 'GMT+2',
  };

  const [preferences, setPreferences] = useState(defaultPreferences);
  const [loading, setLoading] = useState(false);
  const [fetching, setFetching] = useState(true); // Separate state for initial fetch
  const [error, setError] = useState('');
  const token = localStorage.getItem('token');
  const role = localStorage.getItem('role');

  // Fetch user preferences from the backend
  useEffect(() => {
    const fetchPreferences = async () => {
      setFetching(true);
      setError('');
      try {
        const response = await axios.get('http://localhost:5000/api/user/preferences', {
          headers: { Authorization: `Bearer ${token}` },
        });

        // Validate response data
        if (response.data && typeof response.data === 'object') {
          setPreferences(response.data);
        } else {
          setError('Invalid preferences data received.');
        }
      } catch (err) {
        setError('Failed to load preferences. Please try again later.');
      } finally {
        setFetching(false);
      }
    };

    fetchPreferences();
  }, [token]);

  // Handle form field changes
  const handleChange = (e) => {
    const { name, value, type, checked } = e.target;
    setPreferences({
      ...preferences,
      [name]: type === 'checkbox' ? checked : value,
    });
  };

  // Handle form submission
  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);
    setError('');

    try {
      await axios.post('http://localhost:5000/api/user/preferences', preferences, {
        headers: { Authorization: `Bearer ${token}` },
      });
      alert('Preferences updated successfully!');
    } catch (err) {
      setError('Failed to update preferences. Please try again later.');
    } finally {
      setLoading(false);
    }
  };

  // Reset preferences to default values
  const handleReset = () => {
    setPreferences(defaultPreferences);
    setError('');
  };

  return (
    <div className="user-preferences">
      <h1>User Preferences</h1>
      {error && <p className="error">{error}</p>}

      {fetching ? (
        <p>Loading preferences...</p>
      ) : (
        <form onSubmit={handleSubmit}>
          <label>
            Theme:
            <select name="theme" value={preferences.theme} onChange={handleChange}>
              <option value="light">Light</option>
              <option value="dark">Dark</option>
            </select>
          </label>

          <label>
            Notifications:
            <input
              type="checkbox"
              name="notifications"
              checked={preferences.notifications}
              onChange={handleChange}
            />
          </label>

          <label>
            Language:
            <select name="language" value={preferences.language} onChange={handleChange}>
              <option value="en">English</option>
              <option value="es">Spanish</option>
              <option value="fr">French</option>
            </select>
          </label>

          {role === 'admin' && (
            <label>
              Timezone:
              <select name="timezone" value={preferences.timezone} onChange={handleChange}>
                <option value="GMT+2">GMT+2</option>
                <option value="GMT+1">GMT+1</option>
                <option value="GMT">GMT</option>
              </select>
            </label>
          )}

          <div className="buttons">
            <button type="submit" disabled={loading || fetching}>
              {loading ? 'Updating...' : 'Update Preferences'}
            </button>
            <button type="button" onClick={handleReset} disabled={loading || fetching}>
              Reset to Default
            </button>
          </div>
        </form>
      )}
    </div>
  );
};

export default UserPreferences;
