import React from 'react';
import { Link, useNavigate } from 'react-router-dom';
import './Sidebar.css';
import Notifications from './Notifications'; // Ensure this is the correct path to Notifications

const Sidebar = ({ role, handleLogout }) => {
  const navigate = useNavigate();

  const handleLogoutClick = () => {
    handleLogout();
    navigate('/login');
  };

  // Check if the user is authenticated before rendering the sidebar
  const isAuthenticated = !!!role;

  return (
    <div className="sidebar">
      <h3>SecureTradePortal</h3>
      {/* Dashboard Link for All Users */}
      <Link to="/dashboard">Dashboard</Link>
      {/* Admin-Specific Links */}
      {role === 'admin' && (
        <>
          <Link to="/user-management">User Management</Link>
          <Link to="/reports">Reports</Link>
          <Link to="/manage-whats-new">Manage What's New</Link>
        </>
      )}
      {/* Common Links for All Users */}
      <Link to="/transactions">Transactions</Link>
      <Link to="/search-merchants">Search Merchants</Link>
      <Link to="/whats-new">What's New</Link>
      <Notifications /> {/* Notifications component */}
      <button onClick={handleLogoutClick}>Logout</button>
    </div>
  );
};

export default Sidebar;
