// frontend/src/components/Register.js
import React, { useState } from 'react';
import axios from 'axios';
import { useNavigate, Link } from 'react-router-dom';
import './Register.css';

const districtsOfMalawi = [
  'Balaka', 'Blantyre', 'Chikwawa', 'Chiradzulu', 'Chitipa', 'Dedza', 'Dowa', 'Karonga', 'Kasungu', 'Likoma',
  'Lilongwe', 'Machinga', 'Mangochi', 'Mchinji', 'Mulanje', 'Mwanza', 'Mzimba', 'Neno', 'Nkhata Bay', 'Nkhotakota',
  'Nsanje', 'Ntcheu', 'Ntchisi', 'Phalombe', 'Rumphi', 'Salima', 'Thyolo', 'Zomba',
];

const Register = ({ setToken, setRole }) => {
  const [formData, setFormData] = useState({
    name: '',
    email: '',
    password: '',
    role: 'customer',
    membershipType: 'individual',
    contactDetails: {
      whatsapp: '+265',
      location: '',
      areasOfOperation: [],
    },
    businessName: '', // For merchants/couriers
    contactPerson: '', // For merchants/couriers
  });
  const [error, setError] = useState('');
  const navigate = useNavigate();

  const handleChange = (e) => {
    const { name, value } = e.target;
    if (name === 'whatsapp' || name === 'location' || name === 'areasOfOperation') {
      setFormData({
        ...formData,
        contactDetails: {
          ...formData.contactDetails,
          [name]: value,
        },
      });
    } else {
      setFormData({ ...formData, [name]: value });
    }
  };

  const handleCheckboxChange = (e) => {
    const { value, checked } = e.target;
    const areasOfOperation = [...formData.contactDetails.areasOfOperation];
    if (checked) {
      areasOfOperation.push(value);
    } else {
      const index = areasOfOperation.indexOf(value);
      areasOfOperation.splice(index, 1);
    }
    setFormData({
      ...formData,
      contactDetails: {
        ...formData.contactDetails,
        areasOfOperation,
      },
    });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setError('');

    try {
      const response = await axios.post('http://localhost:5000/api/register', formData);
      if (response.data.token) {
        localStorage.setItem('token', response.data.token);
        localStorage.setItem('role', response.data.role);
        setToken(response.data.token);
        setRole(response.data.role);

        // Redirect based on role
        if (formData.role === 'customer') {
          navigate('/customer-dashboard');
        } else if (formData.role === 'merchant' || formData.role === 'courier') {
          navigate('/merchant-dashboard');
        }
      }
    } catch (error) {
      console.error('Registration failed:', error);
      setError('Registration failed. Please try again.');
    }
  };

  return (
    <div className="register">
      <h2>Register</h2>
      {error && <p className="error">{error}</p>}
      <form onSubmit={handleSubmit}>
        {formData.role === 'customer' ? (
          <input
            type="text"
            name="name"
            placeholder="Name"
            value={formData.name}
            onChange={handleChange}
            required
          />
        ) : (
          <>
            <input
              type="text"
              name="businessName"
              placeholder="Business Name"
              value={formData.businessName}
              onChange={handleChange}
              required
            />
            <input
              type="text"
              name="contactPerson"
              placeholder="Contact Person"
              value={formData.contactPerson}
              onChange={handleChange}
              required
            />
          </>
        )}
        <input
          type="email"
          name="email"
          placeholder="Email"
          value={formData.email}
          onChange={handleChange}
          required
        />
        <input
          type="password"
          name="password"
          placeholder="Password"
          value={formData.password}
          onChange={handleChange}
          required
        />
        <select name="role" value={formData.role} onChange={handleChange}>
          <option value="customer">Customer</option>
          <option value="merchant">Merchant</option>
          <option value="courier">Courier</option>
        </select>
        <select name="membershipType" value={formData.membershipType} onChange={handleChange}>
          <option value="individual">Individual (MK6,000/year)</option>
          <option value="merchant">Merchant (MK12,000/year)</option>
          <option value="courier">Courier (MK12,000/year)</option>
          <option value="international_merchant">International Merchant ($20/year)</option>
          <option value="international_customer">International Customer ($5/year)</option>
          <option value="international_courier">International Courier ($20/year)</option>
        </select>
        <input
          type="text"
          name="whatsapp"
          placeholder="WhatsApp Number"
          value={formData.contactDetails.whatsapp}
          onChange={handleChange}
          required
        />
        <select
          name="location"
          value={formData.contactDetails.location}
          onChange={handleChange}
          required
        >
          <option value="">Select Location</option>
          {districtsOfMalawi.map((district) => (
            <option key={district} value={district}>
              {district}
            </option>
          ))}
        </select>
        {(formData.role === 'merchant' || formData.role === 'courier') && (
          <div className="areas-of-operation">
            <h3>Areas of Operation</h3>
            {districtsOfMalawi.map((district) => (
              <label key={district}>
                <input
                  type="checkbox"
                  name="areasOfOperation"
                  value={district}
                  checked={formData.contactDetails.areasOfOperation.includes(district)}
                  onChange={handleCheckboxChange}
                />
                {district}
              </label>
            ))}
          </div>
        )}
        <button type="submit">Register</button>
      </form>
      <p>
        Already have an account? <Link to="/login">Login here</Link>.
      </p>
    </div>
  );
};

export default Register;
