import React from 'react';
import { BrowserRouter as Router, Routes, Route, Navigate } from 'react-router-dom';
import AdminDashboard from './components/AdminDashboard';
import UserPreferences from './components/UserPreferences';
import Login from './components/Login';
import Register from './components/Register';
import Sidebar from './components/Sidebar';
import ProtectedRoute from './components/ProtectedRoute';
import Notifications from './components/Notifications';
import './App.css';

function App() {
  const [token, setToken] = React.useState(null);
  const [role, setRole] = React.useState(null);

  React.useEffect(() => {
    if (token) {
      localStorage.setItem('token', token);
      localStorage.setItem('role', role);
    }
  }, [token, role]);

  const handleLogout = () => {
    localStorage.removeItem('token');
    localStorage.removeItem('role');
    setToken(null);
    setRole(null);
  };

  return (
    <Router>
      <div className="app-container">
        <Sidebar role={role} handleLogout={handleLogout} />
        <div className="content">
          <Routes>
            <Route path="/login" element={<Login setToken={setToken} setRole={setRole} />} />
            <Route path="/register" element={<Register setToken={setToken} setRole={setRole} />} />
            <Route path="/preferences" element={<ProtectedRoute role="customer">
              <UserPreferences />
            </ProtectedRoute>} />
            <Route path="/admin-dashboard" element={<ProtectedRoute role="admin">
              <AdminDashboard />
            </ProtectedRoute>} />
            <Route path="*" element={<Navigate to="/" replace />} />
          </Routes>
        </div>
      </div>
    </Router>
  );
}

export default App;
