// frontend/src/components/Transactions.js
import React, { useEffect, useState } from 'react';
import axios from 'axios';
import './Panel.css';

const Transactions = () => {
  const [transactions, setTransactions] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [description, setDescription] = useState('');
  const [amount, setAmount] = useState('');
  const [paymentMode, setPaymentMode] = useState('');
  const [creatorEmail, setCreatorEmail] = useState('');
  const [creatorPhone, setCreatorPhone] = useState('');
  const [otherPartyEmail, setOtherPartyEmail] = useState('');
  const [otherPartyPhone, setOtherPartyPhone] = useState('');
  const [courier, setCourier] = useState('');
  const token = localStorage.getItem('token');
  const role = localStorage.getItem('role');

  useEffect(() => {
    const fetchTransactions = async () => {
      try {
        const response = await axios.get('http://localhost:5000/api/transactions', {
          headers: { Authorization: `Bearer ${token}` },
        });
        setTransactions(response.data);
      } catch (err) {
        console.error('Error fetching transactions:', err);
        setError('Failed to load transactions.');
      } finally {
        setLoading(false);
      }
    };
    fetchTransactions();
  }, [token]);

  const handleAddTransaction = async (e) => {
    e.preventDefault();
    setError(null); // Reset errors
    if (!description || !amount || !paymentMode || !creatorEmail || !creatorPhone || !otherPartyEmail || !otherPartyPhone) {
      setError('All fields are required.');
      return;
    }
    try {
      const response = await axios.post(
        'http://localhost:5000/api/transactions',
        { description, amount, paymentMode, creatorEmail, creatorPhone, otherPartyEmail, otherPartyPhone, courier },
        {
          headers: { Authorization: `Bearer ${token}` },
        }
      );
      setTransactions([...transactions, response.data]);
      setDescription('');
      setAmount('');
      setPaymentMode('');
      setCreatorEmail('');
      setCreatorPhone('');
      setOtherPartyEmail('');
      setOtherPartyPhone('');
      setCourier('');
      alert('Transaction added successfully.');
    } catch (err) {
      console.error('Error adding transaction:', err);
      setError(err.response?.data?.message || 'Failed to add transaction.');
    }
  };

  if (!token) return <div>You must log in first.</div>;
  if (loading) return <div>Loading...</div>;
  if (error) return <div>{error}</div>;

  return (
    <div className="panel">
      <h1>Transactions</h1>
      {role === 'admin' ? (
        <p>Admin View: All Transactions</p>
      ) : (
        <p>User View: Your Transactions</p>
      )}

      {/* Add Transaction Form */}
      <form onSubmit={handleAddTransaction}>
        <label>
          Description:
          <input
            type="text"
            value={description}
            onChange={(e) => setDescription(e.target.value)}
            placeholder="Description"
            required
          />
        </label>
        <label>
          Amount:
          <input
            type="number"
            value={amount}
            onChange={(e) => setAmount(e.target.value)}
            placeholder="Amount"
            required
          />
        </label>
        <label>
          Payment Mode:
          <select
            value={paymentMode}
            onChange={(e) => setPaymentMode(e.target.value)}
            required
          >
            <option value="">Select Payment Mode</option>
            <option value="TNM Mpamba">TNM Mpamba</option>
            <option value="Airtel Money">Airtel Money</option>
            <option value="National Bank">National Bank</option>
            <option value="Standard Bank">Standard Bank</option>
            <option value="FDH Bank">FDH Bank</option>
            <option value="NBS Bank">NBS Bank</option>
            <option value="Ecobank">Ecobank</option>
            <option value="FMB">FMB</option>
          </select>
        </label>
        <label>
          Your Email:
          <input
            type="email"
            value={creatorEmail}
            onChange={(e) => setCreatorEmail(e.target.value)}
            placeholder="Your Email"
            required
          />
        </label>
        <label>
          Your Phone:
          <input
            type="text"
            value={creatorPhone}
            onChange={(e) => setCreatorPhone(e.target.value)}
            placeholder="Your Phone"
            required
          />
        </label>
        <label>
          Other Party Email:
          <input
            type="email"
            value={otherPartyEmail}
            onChange={(e) => setOtherPartyEmail(e.target.value)}
            placeholder="Other Party Email"
            required
          />
        </label>
        <label>
          Other Party Phone:
          <input
            type="text"
            value={otherPartyPhone}
            onChange={(e) => setOtherPartyPhone(e.target.value)}
            placeholder="Other Party Phone"
            required
          />
        </label>
        <label>
          Courier:
          <input
            type="text"
            value={courier}
            onChange={(e) => setCourier(e.target.value)}
            placeholder="Courier (Optional)"
          />
        </label>
        <button type="submit">Add Transaction</button>
      </form>
      {error && <p className="error">{error}</p>}

      {/* Transactions Table */}
      <table>
        <thead>
          <tr>
            <th>ID</th>
            <th>Description</th>
            <th>Amount (MWK)</th>
            <th>Payment Mode</th>
            <th>Status</th>
          </tr>
        </thead>
        <tbody>
          {transactions.map((transaction) => (
            <tr key={transaction.id}>
              <td>{transaction.id}</td>
              <td>{transaction.description}</td>
              <td>{transaction.amount}</td>
              <td>{transaction.paymentMode}</td>
              <td>{transaction.status}</td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
};

export default Transactions;
