// frontend/src/components/UserPreferences.js
import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './UserPreferences.css';

const UserPreferences = () => {
  const [preferences, setPreferences] = useState({
    theme: 'light',
    notifications: true,
    language: 'en',
    timezone: 'GMT+2',
  });
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState('');
  const token = localStorage.getItem('token');
  const role = localStorage.getItem('role');

  useEffect(() => {
    // Fetch user preferences from the backend
    const fetchPreferences = async () => {
      try {
        const response = await axios.get('http://localhost:5000/api/user/preferences', {
          headers: { Authorization: `Bearer ${token}` },
        });
        setPreferences(response.data);
      } catch (err) {
        setError('Failed to load preferences.');
      }
    };
    fetchPreferences();
  }, [token]);

  const handleChange = (e) => {
    const { name, value, type, checked } = e.target;
    setPreferences({
      ...preferences,
      [name]: type === 'checkbox' ? checked : value,
    });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);
    setError('');

    try {
      await axios.post('http://localhost:5000/api/user/preferences', preferences, {
        headers: { Authorization: `Bearer ${token}` },
      });
      alert('Preferences updated successfully!');
    } catch (err) {
      setError('Failed to update preferences.');
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="user-preferences">
      <h1>User Preferences</h1>
      {error && <p className="error">{error}</p>}
      <form onSubmit={handleSubmit}>
        <label>
          Theme:
          <select name="theme" value={preferences.theme} onChange={handleChange}>
            <option value="light">Light</option>
            <option value="dark">Dark</option>
          </select>
        </label>

        <label>
          Notifications:
          <input
            type="checkbox"
            name="notifications"
            checked={preferences.notifications}
            onChange={handleChange}
          />
        </label>

        <label>
          Language:
          <select name="language" value={preferences.language} onChange={handleChange}>
            <option value="en">English</option>
            <option value="es">Spanish</option>
            <option value="fr">French</option>
          </select>
        </label>

        {role === 'admin' && (
          <label>
            Timezone:
            <select name="timezone" value={preferences.timezone} onChange={handleChange}>
              <option value="GMT+2">GMT+2</option>
              <option value="GMT+1">GMT+1</option>
              <option value="GMT">GMT</option>
            </select>
          </label>
        )}

        <button type="submit" disabled={loading}>
          {loading ? 'Updating...' : 'Update Preferences'}
        </button>
      </form>
    </div>
  );
};

export default UserPreferences;
