import React, { useEffect, useState } from 'react';
import axios from 'axios';
import './Panel.css';

const Transactions = () => {
  const [transactions, setTransactions] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const token = localStorage.getItem('token');
  const role = localStorage.getItem('role');
  const userId = localStorage.getItem('userId'); // Ensure you store the userId in localStorage during login

  // Form state
  const [description, setDescription] = useState('');
  const [amount, setAmount] = useState('');
  const [paymentMode, setPaymentMode] = useState('');
  const [creatorEmail, setCreatorEmail] = useState('');
  const [creatorPhone, setCreatorPhone] = useState('');
  const [otherPartyEmail, setOtherPartyEmail] = useState('');
  const [otherPartyPhone, setOtherPartyPhone] = useState('');
  const [courier, setCourier] = useState('');

  useEffect(() => {
    const fetchTransactions = async () => {
      try {
        const response = await axios.get('http://localhost:5000/api/transactions', {
          headers: { Authorization: `Bearer ${token}` },
        });
        setTransactions(response.data);
      } catch (err) {
        setError('Failed to load transactions.');
      } finally {
        setLoading(false);
      }
    };
    fetchTransactions();
  }, [token]);

  const handleAddTransaction = async (e) => {
    e.preventDefault();
    if (!description || !amount || !paymentMode || !creatorEmail || !creatorPhone || !otherPartyEmail || !otherPartyPhone) {
      setError('All fields are required.');
      return;
    }

    try {
      const response = await axios.post(
        'http://localhost:5000/api/transactions',
        { description, amount, paymentMode, creatorEmail, creatorPhone, otherPartyEmail, otherPartyPhone, courier },
        { headers: { Authorization: `Bearer ${token}` } }
      );
      setTransactions([...transactions, response.data]);

      // Reset form fields
      setDescription('');
      setAmount('');
      setPaymentMode('');
      setCreatorEmail('');
      setCreatorPhone('');
      setOtherPartyEmail('');
      setOtherPartyPhone('');
      setCourier('');
      alert('Transaction added successfully.');
    } catch (err) {
      setError('Failed to add transaction.');
    }
  };

  const updateTransactionStatus = async (id, status) => {
    try {
      await axios.patch(`http://localhost:5000/api/transactions/${id}/status`, { status }, {
        headers: { Authorization: `Bearer ${token}` },
      });
      setTransactions(transactions.map(tx => tx.id === id ? { ...tx, status } : tx));

      // Send notifications
      const transaction = transactions.find(tx => tx.id === id);
      const statusMessage = `Transaction ${transaction.description} is now marked as ${status}.`;
      await axios.post('http://localhost:5000/api/send-notifications', {
        creatorEmail: transaction.creatorEmail,
        creatorPhone: transaction.creatorPhone,
        otherPartyEmail: transaction.otherPartyEmail,
        otherPartyPhone: transaction.otherPartyPhone,
        message: statusMessage,
      });

      alert(`Transaction marked as ${status}`);
    } catch (err) {
      alert('Failed to update transaction status.');
    }
  };

  return (
    <div className="panel">
      <h1>Transactions</h1>
      {role === 'admin' ? (
        <p>Admin View: All Transactions</p>
      ) : (
        <p>User View: Your Transactions</p>
      )}
      {role === 'customer' || role === 'merchant' ? (
        <form onSubmit={handleAddTransaction}>
          <input
            type="text"
            value={description}
            onChange={(e) => setDescription(e.target.value)}
            placeholder="Description"
            required
          />
          <input
            type="number"
            value={amount}
            onChange={(e) => setAmount(e.target.value)}
            placeholder="Amount"
            required
          />
          <select
            value={paymentMode}
            onChange={(e) => setPaymentMode(e.target.value)}
            required
          >
            <option value="">Select Payment Mode</option>
            <option value="TNM Mpamba">TNM Mpamba</option>
            <option value="Airtel Money">Airtel Money</option>
          </select>
          <input
            type="email"
            value={creatorEmail}
            onChange={(e) => setCreatorEmail(e.target.value)}
            placeholder="Your Email"
            required
          />
          <input
            type="text"
            value={creatorPhone}
            onChange={(e) => setCreatorPhone(e.target.value)}
            placeholder="Your Phone"
            required
          />
          <input
            type="email"
            value={otherPartyEmail}
            onChange={(e) => setOtherPartyEmail(e.target.value)}
            placeholder="Other Party Email"
            required
          />
          <input
            type="text"
            value={otherPartyPhone}
            onChange={(e) => setOtherPartyPhone(e.target.value)}
            placeholder="Other Party Phone"
            required
          />
          <input
            type="text"
            value={courier}
            onChange={(e) => setCourier(e.target.value)}
            placeholder="Courier (Optional)"
          />
          <button type="submit">Add Transaction</button>
        </form>
      ) : null}

      <table>
        <thead>
          <tr>
            <th>ID</th>
            <th>Description</th>
            <th>Amount (MWK)</th>
            <th>Payment Mode</th>
            <th>Status</th>
            <th>Actions</th>
          </tr>
        </thead>
        <tbody>
          {transactions.map((transaction) => (
            <tr key={transaction.id}>
              <td>{transaction.id}</td>
              <td>{transaction.description}</td>
              <td>{transaction.amount}</td>
              <td>{transaction.paymentMode}</td>
              <td>{transaction.status}</td>
              <td>
                {role === 'admin' && (
                  <>
                    <button onClick={() => updateTransactionStatus(transaction.id, 'Completed')}>Mark as Completed</button>
                    <button onClick={() => updateTransactionStatus(transaction.id, 'Failed')}>Mark as Failed</button>
                  </>
                )}
              </td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
};

export default Transactions;
