// frontend/src/components/Support.js
import React, { useState } from 'react';
import axios from 'axios';
import './Support.css';

const Support = () => {
  const [activeTab, setActiveTab] = useState('ticket');
  const [ticketDescription, setTicketDescription] = useState('');
  const [email, setEmail] = useState('');
  const [emailMessage, setEmailMessage] = useState('');
  const [error, setError] = useState('');
  const token = localStorage.getItem('token');

  const handleOpenTicket = async (e) => {
    e.preventDefault();
    setError('');

    if (!ticketDescription) {
      setError('Ticket description is required.');
      return;
    }

    try {
      const response = await axios.post('http://localhost:5000/api/support/ticket', {
        description: ticketDescription,
      }, {
        headers: { Authorization: `Bearer ${token}` },
      });
      alert('Ticket opened successfully: ' + response.data.message);
      setTicketDescription('');
    } catch (err) {
      console.error('Error opening ticket:', err);
      setError('Failed to open ticket. Please try again later.');
    }
  };

  const handleSendEmail = async (e) => {
    e.preventDefault();
    setError('');

    if (!email || !emailMessage) {
      setError('Email and message are required.');
      return;
    }

    try {
      await axios.post('http://localhost:5000/api/support/email', {
        email,
        message: emailMessage,
      }, {
        headers: { Authorization: `Bearer ${token}` },
      });
      alert('Email sent successfully to support@securetrade.africa!');
      setEmail('');
      setEmailMessage('');
    } catch (err) {
      console.error('Error sending email:', err);
      setError('Failed to send email. Please try again later.');
    }
  };

  return (
    <div className="support-container">
      <h1>Support</h1>
      {error && <p className="error">{error}</p>}
      <div className="tabs">
        <button onClick={() => setActiveTab('ticket')} className={activeTab === 'ticket' ? 'active' : ''}>
          Open a Ticket
        </button>
        <button onClick={() => setActiveTab('email')} className={activeTab === 'email' ? 'active' : ''}>
          Send an Email
        </button>
      </div>

      {activeTab === 'ticket' && (
        <form onSubmit={handleOpenTicket}>
          <label>
            Ticket Description:
            <textarea
              value={ticketDescription}
              onChange={(e) => setTicketDescription(e.target.value)}
              placeholder="Describe your issue..."
              required
            />
          </label>
          <button type="submit">Submit Ticket</button>
        </form>
      )}

      {activeTab === 'email' && (
        <form onSubmit={handleSendEmail}>
          <label>
            Your Email:
            <input
              type="email"
              value={email}
              onChange={(e) => setEmail(e.target.value)}
              placeholder="Your Email"
              required
            />
          </label>
          <label>
            Message:
            <textarea
              value={emailMessage}
              onChange={(e) => setEmailMessage(e.target.value)}
              placeholder="Write your message..."
              required
            />
          </label>
          <button type="submit">Send Email</button>
        </form>
      )}
    </div>
  );
};

export default Support;
