// frontend/src/components/Reports.js
import React, { useEffect, useState } from 'react';
import axios from 'axios';
import './Panel.css';

const Reports = () => {
  const [reports, setReports] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const token = localStorage.getItem('token');

  useEffect(() => {
    const fetchReports = async () => {
      if (!token) {
        setError('You must log in first.');
        setLoading(false);
        return;
      }
      try {
        const response = await axios.get('http://localhost:5000/api/reports', {
          headers: { Authorization: `Bearer ${token}` },
        });
        setReports(response.data);
      } catch (err) {
        console.error('Error fetching reports:', err);
        setError('Failed to load reports.');
      } finally {
        setLoading(false);
      }
    };
    fetchReports();
  }, [token]);

  const handleDownload = async (id) => {
    try {
      const response = await axios.get(`http://localhost:5000/api/reports/download/${id}`, {
        headers: { Authorization: `Bearer ${token}` },
        responseType: 'blob',
      });

      const url = window.URL.createObjectURL(new Blob([response.data]));
      const link = document.createElement('a');
      link.href = url;
      link.setAttribute('download', `report-${id}.csv`);
      document.body.appendChild(link);
      link.click();
      document.body.removeChild(link);
    } catch (err) {
      console.error('Error downloading report:', err);
      setError('Failed to download report.');
    }
  };

  return (
    <div className="panel">
      <h1>Reports</h1>
      {loading ? (
        <p>Loading...</p>
      ) : error ? (
        <p>{error}</p>
      ) : (
        <ul>
          {reports.map((report) => (
            <li key={report.id} className="report-card">
              <h2>{report.title}</h2>
              <p>Date: {new Date(report.date).toLocaleDateString()}</p>
              <p>{report.content}</p>
              <button onClick={() => handleDownload(report.id)} className="download-csv">
                Download CSV
              </button>
            </li>
          ))}
        </ul>
      )}
    </div>
  );
};

export default Reports;
