// frontend/src/components/TradingInsights.js
import React, { useEffect, useState } from 'react';
import axios from 'axios';
import './AdminDashboard.css'; // Use centralized CSS

const TradingInsights = () => {
  const [tradingInsights, setTradingInsights] = useState({
    tradeVolume: [], // Default empty array
    successRate: [], // Default empty array
  });

  useEffect(() => {
    axios.get('http://localhost:5000/api/analytics/trading-insights')
      .then(response => setTradingInsights(response.data))
      .catch(error => console.error('Error fetching trading insights:', error));
  }, []);

  return (
    <div className="trading-insights">
      <h2>Trading Insights</h2>

      {/* Trade Volume */}
      <div className="trade-volume">
        <h3>Trade Volume</h3>
        {tradingInsights.tradeVolume.length > 0 ? (
          <ul>
            {tradingInsights.tradeVolume.map((item, index) => (
              <li key={index}>
                {item.asset}: {item.volume}
              </li>
            ))}
          </ul>
        ) : (
          <p>No trade volume data available.</p>
        )}
      </div>

      {/* Success Rate */}
      <div className="success-rate">
        <h3>Success Rate</h3>
        {tradingInsights.successRate.length > 0 ? (
          <ul>
            {tradingInsights.successRate.map((item, index) => (
              <li key={index}>
                {item.name}: {item.value}%
              </li>
            ))}
          </ul>
        ) : (
          <p>No success rate data available.</p>
        )}
      </div>
    </div>
  );
};

export default TradingInsights;
