// frontend/src/components/Transactions.js
import React, { useEffect, useState } from 'react';
import axios from 'axios';
import './Panel.css';

const Transactions = () => {
  const [transactions, setTransactions] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const token = localStorage.getItem('token');
  const role = localStorage.getItem('role');

  useEffect(() => {
    const fetchTransactions = async () => {
      try {
        const response = await axios.get('http://localhost:5000/api/transactions', {
          headers: { Authorization: `Bearer ${token}` },
        });
        setTransactions(response.data);
      } catch (err) {
        setError('Failed to load transactions.');
      } finally {
        setLoading(false);
      }
    };
    fetchTransactions();
  }, [token]);

  return (
    <div className="panel">
      <h1>Transactions</h1>
      {role === 'admin' ? (
        <p>Admin View: All Transactions</p>
      ) : (
        <p>User View: Your Transactions</p>
      )}

      {loading ? (
        <p>Loading...</p>
      ) : error ? (
        <p>{error}</p>
      ) : (
        <table>
          <thead>
            <tr>
              <th>ID</th>
              <th>Description</th>
              <th>Amount (MWK)</th>
              <th>Payment Mode</th>
              <th>Status</th>
              <th>Actions</th>
            </tr>
          </thead>
          <tbody>
            {transactions.map((transaction) => (
              <tr key={transaction.id}>
                <td>{transaction.id}</td>
                <td>{transaction.description}</td>
                <td>{transaction.amount}</td>
                <td>{transaction.paymentMode}</td>
                <td>{transaction.status}</td>
                <td>
                  {role === 'admin' && (
                    <>
                      <button onClick={() => updateTransactionStatus(transaction.id, 'Completed')}>Mark as Completed</button>
                      <button onClick={() => updateTransactionStatus(transaction.id, 'Failed')}>Mark as Failed</button>
                    </>
                  )}
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      )}
    </div>
  );
};

export default Transactions;
